const { createAnimatedGif } = require('./gifUtils');

// Test data dengan gambar sederhana
const testPhotos = [
  {
    original: 'data:image/jpeg;base64,',
    processed: 'data:image/jpeg;base64,',
    mode: 'test',
    timestamp: new Date().toISOString()
  }
];

async function testNewGif() {
  try {
    console.log('üß™ Testing new animated GIF implementation...');
    const result = await createAnimatedGif(testPhotos);
    console.log(`‚úÖ Animated GIF created successfully (${result.length} bytes)`);
    
    // Save test result
    const fs = require('fs');
    const testPath = './test_new_animated.gif';
    fs.writeFileSync(testPath, result);
    console.log(`‚úÖ Test GIF saved to: ${testPath}`);
    console.log('üéâ Test passed!');
  } catch (error) {
    console.error('‚ùå Test failed:', error.message);
    console.error('Full error:', error);
  }
}

testNewGif();
